using namespace std;

int main()
{
  FILE *gnuplot= popen("gnuplot -persistent","w");

fprintf(gnuplot,"set title 'Draw Line'\n");
fprintf(gnuplot,"set xlabel 'X-axis'\n");
fprintf(gnuplot,"set ylabel 'Y-axis'\n");

cout<<"Point A"<<endl;
float a,b;
cin>>a>>b;

float p,q;
cin>>p>>q;

fprintf(gnuplot,"plot [%f:%f] (%f*(x-%f)) \n",a,p,
((q-b)/(p-a)),a);
fprintf(gnuplot,"plot '-' using  1:2 title 'points' with line points pointtype 7 pointsize 1.5\n");

for(int i=1;i<=8;i++)
{
float x=a+(p-a)*i/9.0;
float y=((q-b)/(p-a))*(x-a)+b;

fprintf(gnuplot,"%d %d\n",(int)x,(int)y);
}
pclose(gnuplot);

return 0;

}
